CREATE VIEW FI.V_SAP_FI_CURRENCY_EXCHANGE AS select 
	right(tcurr.gdatu,4)+substring(tcurr.GDATU,3,2) + left(tcurr.GDATU,2)  as valid_from
	, min(coalesce(valid_to,'99991231')) as valid_to
	, tcurr.fcurr as fcurr
	, tcurr.tcurr as tcurr
	, cast(tcurr.ukurs as numeric(9,2)) as ukurs
from fi.tcurr tcurr
	left outer join 
		(select distinct right(gdatu,4)+substring(GDATU,3,2) + left(GDATU,2)  as valid_to, fcurr, tcurr
		from fi.tcurr
		where kurst ='M' and  tcurr='eur'
		and isnumeric(ukurs)=1)aux
	on (aux.FCURR =tcurr.FCURR and aux.TCURR=tcurr.TCURR and  (right(gdatu,4)+substring(GDATU,3,2) + left(GDATU,2) ) <   aux.valid_to)
where 
	tcurr.kurst ='M' 
	and  tcurr.tcurr='eur'
	and isnumeric(tcurr.ukurs)=1
	and right(tcurr.gdatu,4) >='2017'
group by 
	right(tcurr.gdatu,4)+substring(tcurr.GDATU,3,2) + left(tcurr.GDATU,2)  
	, tcurr.fcurr 
	, tcurr.tcurr 
	, cast(tcurr.ukurs as numeric(9,2));

