if zn([Compensacion CRM])+zn([Reembolso CRM])-zn([Importe SAP])=0
then "caso OK"
ELSEIF (zn([Compensacion CRM])+zn([Reembolso CRM]))>zn([Importe SAP])
then "CRM>SAP"
else "SAP>CRM"
END
*/
--consulta tableau
CREATE view  [dbo].[v_qa_crm_sap] as
select isnull(s.sr_num, c.texto) as sr, s.fecha_cierre, s.fecha_Creacion,
case when isnull(s.COMPENSACIN,0)+isnull(s.REEMBOLSO,0)>0 then 'coste crm' else 'sin coste CRM' end as flag_SR_coste_CRM,
case when s.SR_NUM is null then 'no CRM' else 'CRM' end as flag_CRM,
case when c.texto is null then 'no SAP' else 'SAP' end as flag_SAP,
case when  isnull(s.COMPENSACIN,0)-isnull(c.Compensacin_SAP,0)-isnull(c.overbooking_sap,0)=0 then 'caso OK'
when  isnull(s.COMPENSACIN,0)>isnull(c.Compensacin_SAP,0)+isnull(c.overbooking_sap,0) then 'CRM>SAP'
else 'SAP>CRM' end  as balance_crm_sap_comp,
case when  isnull(s.COMPENSACIN,0)+isnull(s.REEMBOLSO,0)-isnull(c.importe_ml,0)=0 then 'caso OK'
when  isnull(s.COMPENSACIN,0)+isnull(s.REEMBOLSO,0)>isnull(c.importe_ml,0) then 'CRM>SAP'
else 'SAP>CRM' end  as balance_crm_sap
,isnull(s.COMPENSACIN,0) as Compensacion_CRM, isnull(c.Compensacin_SAP,0) as Compensacin_SAP, 
isnull(c.overbooking_sap,0) as overbooking_sap, isnull(s.REEMBOLSO,0) as reembolso_CRM, isnull(c.reembolso_SAP,0) as reembolso_SAP
--, COUNT(*) as registros, sum(s.COMPENSACIN) as compensacionCRM, sum(c.Compensacin_SAP) as Compensacin_SAP,
 --sum(c.overbooking_sap) as overbooking_SAP,  sum(s.REEMBOLSO) as reembolso_crm, sum(c.reembolso_SAP) as reembolso_SAP 
from (select * from vSRs_total --where fecha_cierre between '20161101' and '20161115'
) s 
full join 
fbl3n_tmp_calc_imp c on s.sr_num=c.texto;

