	OPT_VIM_1HEAD.XBLNR as [Reference Document Number],
	0  as [Gross invoice amount in document currency],
	OPT_VIM_1HEAD.WAERS as [Currency Key],
	OPT_VIM_1HEAD.REQUISITIONER as [Requisitioner],
	OPT_VIM_1HEAD.SGTXT as [Item Text],
	0  as [Invoice Header Net Amount],
	0  as [Invoice Header Gross Amount],
	0 as [Invoice Header VAT Amount],
	OPT_VIM_1HEAD.TAX_CODE as [Header Tax Code],
	OPT_VIM_1HEAD.ZZDUE_DATE  as [Due Date],
	--ITEM
	OPT_VIM_1ITEM.EBELN as [Purchasing Document Number],
	OPT_VIM_1ITEM.EBELP as [Item Number of Purchasing Document],
	OPT_VIM_1ITEM.SHKZG as [Debit/Credit Indicator],
	case when OPT_VIM_1ITEM.SHKZG = 'H' then cast(OPT_VIM_1ITEM.MENGE as decimal (14,2))*(-1) else cast(OPT_VIM_1ITEM.MENGE as decimal (14,2)) end as [Quantity],
	OPT_VIM_1ITEM.BSTME as [Purchase Order Unit of Measure],
	case when OPT_VIM_1ITEM.SHKZG = 'H' then cast(OPT_VIM_1ITEM.ZMENGE as decimal (14,2))*(-1) else cast(OPT_VIM_1ITEM.ZMENGE as decimal (14,2)) end  as [Quantity2],
	OPT_VIM_1ITEM.ZBSTME as [Purchase Order Unit of Measure2],
	case when OPT_VIM_1ITEM.SHKZG = 'H' then cast(OPT_VIM_1ITEM.WRBTR as decimal (14,2))*(-1) else cast(OPT_VIM_1ITEM.WRBTR as decimal (14,2)) end  as [Invoice Amount in Document Currency],
	case when OPT_VIM_1ITEM.SHKZG = 'H' then cast(OPT_VIM_1ITEM.NETPR as decimal (14,2))*(-1) else cast(OPT_VIM_1ITEM.NETPR as decimal (14,2)) end  as [Net Price in Purchasing Document (in Document Currency)],
	case when OPT_VIM_1ITEM.SHKZG = 'H' then cast(OPT_VIM_1ITEM.TAX_AMOUNT as decimal (14,2))*(-1) else cast(OPT_VIM_1ITEM.TAX_AMOUNT as decimal (14,2)) end  as [Tax Amount in Document Currency],
	OPT_VIM_1ITEM.TAX_CODE1 as [Tax Code],
	OPT_VIM_1ITEM.MWSKZ as [Sales Tax Code],
	OPT_VIM_1ITEM.ZVUELO as [Flight code],
	OPT_VIM_1ITEM.ZMATRI as [Plane ID],
	OPT_VIM_1ITEM.ZAIMPU as [Airport of imputation],
	OPT_VIM_1ITEM.ZDESTI as [Arrival airport],
	OPT_VIM_1ITEM.ZORIGE as [Departure Airport],
	OPT_VIM_1ITEM.ZFECHA_VUELO   as [Flight Date],
	OPT_VIM_1ITEM.ZHORA as [Check out time],
	OPT_VIM_1ITEM.ZIMPORTE as [Line-level Amount],
	OPT_VIM_1ITEM.ZTAVION as [Type of plane],
	OPT_VIM_1ITEM.ZESTAD as [State of finding differences],
	OPT_VIM_1ITEM.EKGRP as [Purchasing Group],
	--DIMENSIONS
	SKAt.SAKNR  as [G/L Account Number],
	SKAT.TXT20 as [G/L account short text],
	SKA1.KTOKS as [G/L Account Group],
	SKA1.XBILK as [IS BALANCE?],
	SKB1.FIPLS as [Financial Budget Item],
	SKB1.FSTAG as [Field status group],
	SKB1.MITKZ as [is reconciliation account?],
	SKB1.MWSKZ as [Tax Category in Account Master Record],
	LFA1.LIFNR as [Vendor Account Number],
	LFA1.LAND1 as [Vendor Country Key],
	LFA1.NAME1 as [Vendor Name 1],
	LFA1.REGIO as [Vendor Region],
	LFA1.ORT01 as [Vendor City],
	LFA1.KONZS as [Vendor Group key],
	LFA1.KTOKK as [Vendor account group],
	LFB1.ZAHLS as [Block key for payment],
	LFB1.ZTERM as [Terms of Payment],
	LFB1.AKONT as [Reconciliation Account],
	LFB1.FDGRV	as [Planning group],
	MAKT.MATNR as [Material Number],
	MAKT.MAKTX as [Material Description],
	MARA.MTART as [Material Type],
	MARA.MATKL as [Material Group],
	MARA.ATTYP as [Material Category],
	MARA.MEINS as [Material Base Unit of Measure],
	CSKT.KOKRS as [Controlling Area],
	CSKT.KOSTL as [Cost Center],
	CSKT.DATBI as [CC Valid To Date],
	CSKT.KTEXT as [CC General Name],
	CSKT.LTEXT as [CC Description],
	case when [OPT_VIM_1HEAD].waers='EUR' then 1 else cast(tcurr.[ukurs] as decimal (5,2))  end as [EUR Exchange Rate]
from  
	 [FI].[OPT_VIM_1HEAD] OPT_VIM_1HEAD  
	left outer JOIN [FI].[OPT_VIM_1ITEM]  OPT_VIM_1ITEM 	
		on  OPT_VIM_1ITEM.DOCID=OPT_VIM_1HEAD.DOCID 
			--DIMENSIONS	
	left outer join FI.SKAt SKat --G/L Account Master (Chart of Accounts) 
		on  OPT_VIM_1ITEM.hkont  =SKAt.SAKNR and skat.SPRas = 'S'  and skat.KTOPL='PCVU'
	left outer JOIN FI.SKA1 -- G/L Account Master
		on SKAt.SAKNR=SKA1.SAKNR  and ska1.KTOPL='PCVU'
	left outer JOIN FI.SKb1 -- G/L Account Master (company code)
		on SKAt.SAKNR=SKb1.SAKNR  and OPT_VIM_1HEAD.bukrs=SKb1.bukrs 
	left outer join FI.LFB1 LFB1 --Vendor Master (Company Code)
		on LFB1.LIFNR=OPT_VIM_1HEAD.LIFNR and LFB1.BUKRS = OPT_VIM_1HEAD.bukrs
	left outer join FI.LFA1 LFA1 --Vendor Master (General Section)
		on LFB1.LIFNR=LFA1.LIFNR
	left outer join  FI.makt MAKT  --Material Descriptions
		on MAKT.matnr=OPT_VIM_1ITEM.matnr  and makt.SPRas='s'
	left outer join FI.MARA MARA --General Material Data
		on MAKT.MATNR=MARA.MATNR
	left outer join FI.CSKT CSKT --Cost Center Texts 
		on rtrim(ltrim(OPT_VIM_1ITEM.KOSTL))=RTRIM(ltrim( CSKT.KOSTL)) and CSKT.KOKRS= '0100' and cSKT.SPRas = 'S'  and datbi='99991231'
	left outer join [FI].[OPT_VIM_T101T] OPT_VIM_T101T
		on OPT_VIM_T101T.statusid = OPT_VIM_1HEAD.STATUS and OPT_VIM_T101T.SPRAS ='E'
	left outer join [FI].[V_SAP_FI_CURRENCY_EXCHANGE]  tcurr
		on (tcurr.fcurr=OPT_VIM_1HEAD.WAERS  and (case when OPT_VIM_1HEAD.STATUS = '15' then BUDAT else INDEX_DATE end ) between tCURR.valid_from and TCURR.valid_to);

